import React, {useState, useEffect} from 'react';
import { Card, Button, Container, Row, Col, Image } from 'react-bootstrap';
import axios from 'axios';
import products from '../product';
import './ProductPage.css';


export function ProductPage({ match, history }) {
    const pk = match.params.id;
    const product = products[1];
    
    /*
    const [product, setProduct] = useState({})

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/products/${pk}`)
        .then((response) => {
            setProduct(response.data)
        })
    })
    */
    
    return (
        <Container className='mt-5'>
            <Button variant='outline-success' href='/'>Back</Button>

            <Row class='mt-5'>
                <Col xs={6}>
                    <Image className='product-img' src={`http://127.0.0.1:8000${product.image}`} rounded/>
                </Col>

                <Col xs={6}>
                    <Row>
                        <h2>{product.name}</h2>
                    </Row>
                    <Row>
                        <div className='my-3'>
                            {product.rating} from {product.numReivews} reviews
                        </div>
                    </Row>
                    <Row>
                        $<h1>{product.price}</h1>
                    </Row>
                    <Row>
                        <p>{product.description}</p>
                    </Row>

                </Col>
            </Row>
        </Container>
    )
}
