import React, {useState, useEffect} from 'react';
import { Card, Button, Container, Row, Col, Image, Dropdown, ListGroup, Form } from 'react-bootstrap';
import axios from 'axios';
import products from '../product';
import './ProductPage.css';


export function ProductPage({ match, history }) {
    const pk = match.params.id;
    // const product = products[2];
    
    const [product, setProduct] = useState({})
    const [qty, setQty] = useState(0)

    useEffect(() => {
        getProductDetails();

    }, []);

    const getProductDetails = () => {
        axios.get(`http://127.0.0.1:8000/api/products/${pk}`)
        .then((response) => {
            setProduct(response.data)
        })
    }

    /*
    const addToCartHandler = () => {
        axios.get('http://127.0.0.1:8000/api/get_order/')
        .then((response) => {
            console.log(response.data);
        })
    }
    */

    const qtyChangeHandler = (e) => {
        // setQty(e.target.value)
        console.lot('qty change')
        console.log(e);
    }

    


    /*
    const addToCartHandler = () => {
        axios.post('http://127.0.0.1:8000/api/add_product_to_order/'. {
            id: product,
            qty: 
        });
    }
    */
    
    return (
        <div className='mt-5 product-display-container'>

            <Button variant='outline-success' href='/'>Back</Button>

            <Row className='product-display'>
                <Col xs={4}>
                    <Image className='product-img' src={`http://127.0.0.1:8000${product.image}`} rounded/>
                </Col>

                <Col xs={4} className='product-info'>
                    <Row>
                        <h2>{product.name}</h2>
                    </Row>
                    
                    <Row>
                        <div className='my-3'>
                            {product.rating} from {product.numReviews} reviews
                        </div>
                    </Row>
                    
                    <Row>
                        <h1>${product.price}</h1>
                    </Row>
                    
                    <Row>
                        <p>{product.description}</p>
                    </Row>

                </Col>

                <Col xs={3}>
                    <ListGroup>
                        <ListGroup.Item>
                            <Row>
                                <h1>Subtotal</h1>
                            </Row>

                            <Row className='qty-text mt-4'>
                                <Col xs={2}>
                                    Qty:
                                </Col>

                                <Col xs={10}>
                                    <Form.Control
                                        as="select"
                                        value={qty}
                                        onChange={(e) => setQty(e.target.value)}
                                    >
                                        {
                                            [...Array(product.countInStock).keys()].map((x) => (
                                                    <option key={x+1} value={x+1}>
                                                        {x + 1}
                                                    </option>
                                                ))
                                            }
                                    </Form.Control>
                                </Col>
                            </Row>

                            <Row className='price-text mt-2'>
                                <Col xs={2}>
                                    Price: 
                                </Col>

                                <Col xs={10}>
                                    ${product.price}
                                </Col>

                            </Row>

                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Button 
                                variant='dark' 
                                size='lg' 
                                className='w-100'
                                disabled={product.countInStock === 0}
//                                href='/cart'
//                                onClick={addToCartHandler}
                                >Add to Cart</Button>
                        </ListGroup.Item>
                    </ListGroup>   
                </Col>

            </Row>

           
        </div>
    )
}

